mapper.update:
update BOARD
set title=#{boardTitle},
    content=#{content},
    "date" = #{date}
where id = #{id}

update BOARD
set hit=hit + 1
where ID = #{id}

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() over (order by id desc ) AS num, b.* FROM board b WHERE rownum <= #{endList}
AND b.title like '%' || #{keyword} || '%'
AND b.CONTENT like '%' || #{keyword} || '%'
AND b.writer like '%' || #{keyword} || '%'
) WHERE #{startList} < num
